import React, { useState } from 'react';
import axios from 'axios';

function Raw() {

    //whether the data is being loaded from the API or not 
    const [loading, setLoading] = useState(false);

    //an array that returns the data from API 
    const [resources, setResources] = useState([]);

    //can be used to select specifc resources  
    const [selectedResources, setSelectedResources] = useState(null);

    //indicates to whether display the the list of resources or just the details 
    const [showApp, setShowApp] = useState(true);



    //async function that sends request to get the list of resources
    const displayResources = async () => {
        setLoading(true);
        const response = await axios.get('https://engineering-task.elancoapps.com/api/raw');
        setResources(response.data);
        setLoading(false);
    };


    //allows to clear the selected resources, and load the orignal page 
    const goBack = () => {
        setSelectedResources(null);
        setResources([]);
        setShowApp(true);
    };




    return (

        <div className='main'>
            <h1>Raw</h1>

            {/* Render a button to retrieve data from API if showApp is true */}
            {showApp && <button className='btn' onClick={displayResources}>Load Raws</button>}

            {/* Renders a paragraph if loading is true */}
            {loading ? <p>Loading...</p> :

                //also, if showApp is true it renders a list of resources and a button to view the deatials of each resources.
                //.map allows us to iterate through the array
                showApp ? (
                    <ul>
                        {resources.map((resource) => (
                            <li >
                                {resource}{" "}

                            </li>
                        ))}
                    </ul>
                ) : null}


        </div >
    );
}

export default Raw;
