import './App.css';

import React, { useState, useEffect } from 'react';
import axios from 'axios';


function App() {
  const [data, setData] = useState([]);

  const fetchData = () => {
    axios.get('http://localhost:4000/applications')
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }
  useEffect(() => {
    fetchData();
  }, []);
  return (
    <div>
      <h1>Applications</h1>
      <button onClick={fetchData}>Fetch Data</button>
      <table style={{ border: '1px solid black', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ backgroundColor: '#ddd' }}>
            <th style={{ border: '1px solid black', padding: '5px' }}>ID</th>
            <th style={{ border: '1px solid black', padding: '5px' }}>Name</th>
            <th style={{ border: '1px solid black', padding: '5px' }}>Description</th>
          </tr>
        </thead>
        <tbody>
          {data.map(application => (
            <tr key={application.id} style={{ border: '1px solid black' }}>
              <td style={{ border: '1px solid black', padding: '5px', color: 'black' }}>{application.id}</td>
              <td style={{ border: '1px solid black', padding: '5px', color: 'black' }}>{application.name}</td>
              <td style={{ border: '1px solid black', padding: '5px', color: 'black' }}>{application.description}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>

  );
}

export default App;
