import '../Css/Applications.css';
import React, { useState } from 'react';
import axios from 'axios';



function Applications() {
    //whether the data is being loaded from the API or not 
    const [loading, setLoading] = useState(false);

    //an array that returns the data from API 
    const [applications, setApplications] = useState([]);

    //can be used to select specifc applications 
    const [selectedApplication, setSelectedApplication] = useState(null);

    //shows the details of the selected application 
    const [applicationDetail, setApplicationDetail] = useState(null);

    //indicates to whether display the the list of applications or just the details 
    const [showApp, setShowApp] = useState(true);



    //async function that sends request to get the values 
    const displayApplication = async () => {
        setLoading(true);
        const response = await axios.get('https://engineering-task.elancoapps.com/api/applications');
        setApplications(response.data);
        setLoading(false);
    };
    //async function that sends request to get the details of the selected application
    const chooseApplication = async (application) => {
        setSelectedApplication(application);
        setLoading(true);
        const response = await axios.get(`https://engineering-task.elancoapps.com/api/applications/${application}`);
        setApplicationDetail(response.data);
        setLoading(false);
        setShowApp(false);
    };

    //allows to clear the selected applition, and load applications again
    const goBack = () => {
        setSelectedApplication(null);
        setApplications([]);
        setShowApp(true);
    };

    return (
        <div className='main'>
            <h1>Applications</h1>


        </div >
    );
}

export default Applications;
