import React, { useState } from 'react';
import axios from 'axios';

function Raw() {
    const [data, setData] = useState([]);
    const [sortOption, setSortOption] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredData, setFilteredData] = useState([]);

    const handleButtonClick = async () => {
        const response = await axios.get('https://engineering-task.elancoapps.com/api/raw');
        setData(response.data);
        setFilteredData(response.data);
    };

    const handleSortOptionChange = (option) => {
        setSortOption(option);
        sortData(option, filteredData);
    };

    const handleSearchInputChange = (event) => {
        setSearchTerm(event.target.value);
        filterData(event.target.value, data);
    };

    const sortData = (option, data) => {
        let sortedData = data.slice();
        switch (option) {
            case 'name-asc':
                sortedData.sort((a, b) => a.name.localeCompare(b.name));
                break;
            case 'name-desc':
                sortedData.sort((a, b) => b.name.localeCompare(a.name));
                break;
            case 'price-asc':
                sortedData.sort((a, b) => a.price - b.price);
                break;
            case 'price-desc':
                sortedData.sort((a, b) => b.price - a.price);
                break;
            default:
                break;
        }
        setFilteredData(sortedData);
    };

    const filterData = (term, data) => {
        let filteredData = data.filter((item) =>
            item.name.toLowerCase().includes(term.toLowerCase())
        );
        sortData(sortOption, filteredData);
    };

    return (
        <div className="main">
            <h1>Raw Data</h1>
            <button className="btn" onClick={handleButtonClick}>
                Get Data
            </button>
            <div className="sort">
                <label htmlFor="sort-select">Sort by:</label>
                <select id="sort-select" onChange={(e) => handleSortOptionChange(e.target.value)}>
                    <option value="">-- Select an option --</option>
                    <option value="name-asc">Name (A-Z)</option>
                    <option value="name-desc">Name (Z-A)</option>
                    <option value="price-asc">Price (Low to High)</option>
                    <option value="price-desc">Price (High to Low)</option>
                </select>
            </div>
            <div className="search">
                <label htmlFor="search-input">Search:</label>
                <input id="search-input" type="text" onChange={handleSearchInputChange} />
            </div>
            {filteredData && <pre>{JSON.stringify(filteredData, null, 2)}</pre>}
        </div>
    );

}

export default Raw;




