import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {

  const [applications, setApplications] = useState([]);
  const [showAll, setShowAll] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:4000/applications')
      .then(response => {
        setApplications(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  function handleShowAllClick() {
    setShowAll(true);
  }

  return (

    <div>
      <h1>Applications</h1>
      <ul>
        {showAll ? ( // add this conditional rendering
          applications.map(application => (
            <li key={application.id}>{application.name}</li>
          ))
        ) : (
          <li>Click the button to show all applications</li>
        )}
      </ul>
      <button onClick={handleShowAllClick}>Show all applications</button> {/* add this button */}
    </div>
  );
}

export default App;
