import '../Css/Applications.css';
import React, { useState } from 'react';
import axios from 'axios';

function Resources() {
    //whether the data is being loaded from the API or not 
    const [loading, setLoading] = useState(false);

    //an array that returns the data from API 
    const [resources, setResources] = useState([]);

    //can be used to select specifc applications 
    const [selectedResources, setSelectedResources] = useState(null);

    //shows the details of the selected application 
    const [resourcesDetail, setResourcesDetail] = useState(null);

    //indicates to whether display the the list of applications or just the details 
    const [showApp, setShowApp] = useState(true);



    //async function that sends request to get the values 
    const displayResources = async () => {
        setLoading(true);
        const response = await axios.get('https://engineering-task.elancoapps.com/api/resources');
        setResources(response.data);
        setLoading(false);
    };
    //async function that sends request to get the details of the selected application
    const chooseResources = async (application) => {
        setSelectedResources(application);
        setLoading(true);
        const response = await axios.get(`https://engineering-task.elancoapps.com/api/resources/${application}`);
        setResourcesDetail(response.data);
        setLoading(false);
        setShowApp(false);
    };

    //allows to clear the selected applition, and load applications again
    const goBack = () => {
        setSelectedResources(null);
        setResources([]);
        setShowApp(true);
    };

    return (
        <div className='main'>
            <h1>Resources</h1>

            {/* Render a button to retrieve data from API if showApp is true */}
            {showApp && <button className='btn' onClick={displayResources}>Load Resources</button>}

            {/* Renders a paragraph if loading is true */}
            {loading ? <p>Loading...</p> :

                // Render a list of applications along with a button to see the details of each application
                // The applications are displayed as a list wrapped around a "li"
                // .map is used to iterate through the array
                showApp ? (
                    <ul>
                        {resources.map((application) => (
                            <li key={application}>
                                {application}{" "}
                                <button className='btn' onClick={() => chooseResources(application)}>
                                    View Details
                                </button>
                            </li>
                        ))}
                    </ul>
                ) :

                    // Display application information if applicationDetail is true
                    // If loading is true, show a message to let the user know the application information is loading
                    // If applicationDetail is false, show a message to let the user know to select an application
                    <div>
                        <h2>{selectedResources}</h2>
                        <button className='btn' onClick={goBack}>Go Back</button>
                        {loading ? <p>Loading...</p> :
                            resourcesDetail ? (
                                <div>
                                    <pre>{JSON.stringify(resourcesDetail, null, 5)}</pre>
                                </div>
                            ) : <p>Loading application information...</p>}
                    </div>
            }
        </div >
    );
};

export default Resources;
