import '../Css/Applications.css';
import React, { useState } from 'react';
import axios from 'axios';



function Applications() {
    const [isLoading, setIsLoading] = useState(false);
    const [applications, setApplications] = useState([]);
    const [selectedApplication, setSelectedApplication] = useState(null);
    const [applicationInfo, setApplicationInfo] = useState(null);

    const loadApplications = async () => {
        setIsLoading(true);
        const response = await axios.get('https://engineering-task.elancoapps.com/api/applications');
        setApplications(response.data);
        setIsLoading(false);
    };

    const selectApplication = async (application) => {
        setSelectedApplication(application);
        setIsLoading(true);
        const response = await axios.get(`https://engineering-task.elancoapps.com/api/applications/${application}`);
        setApplicationInfo(response.data);
        setIsLoading(false);
        setApplications([application]);
    };

    const goBack = () => {
        setSelectedApplication(null);
        setApplications([]);
    };

    return (
        <div className="container">
            <h1>Applications</h1>
            <button className="load-btn" onClick={loadApplications}>Load Applications</button>
            {isLoading ? <p>Loading...</p> : (
                <ul>
                    {applications.map((application) => (
                        <li key={application}>
                            {application}{' '}
                            <button className="view-btn" onClick={() => selectApplication(application)}>View Details</button>
                        </li>
                    ))}
                </ul>
            )}
            {selectedApplication && (
                <div className="details">
                    <div className="back">
                        <button onClick={goBack}>Back</button>
                    </div>
                    <h2>{selectedApplication}</h2>
                    {isLoading ? <p>Loading application information...</p> : applicationInfo ? (
                        <div className="info">
                            {Object.entries(applicationInfo).map(([key, value]) => (
                                <div className="data" key={key}>
                                    <span>{key}: </span>
                                    <span>{value}</span>
                                </div>
                            ))}
                        </div>
                    ) : (
                        <p>Loading application information...</p>
                    )}
                </div>
            )}
        </div>
    );
}

export default Applications;






