import './App.css';
import React, { useState } from 'react';


function App() {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchTermChange = event => {
    setSearchTerm(event.target.value);
  };

  const handleClick = async () => {
    setIsLoading(true);
    const response = await fetch("https://engineering-task.elancoapps.com/api/applications");
    const json = await response.json();
    setData(json);
    setIsLoading(false);
  };

  const searchResults = data.filter(app => app.name.includes(searchTerm));

  return (
    <div>
      <h1>Applications</h1>
      <button onClick={handleClick}>Load Data</button>
      <div>
        <label htmlFor="search">Search:</label>
        <input type="text" id="search" value={searchTerm} onChange={handleSearchTermChange} />
      </div>
      {isLoading ? (
        <p>Loading data...</p>
      ) : data.length > 0 ? (
        <div>
          {searchTerm ? (
            <div>
              <p>Search results for "{searchTerm}":</p>
              <pre>{JSON.stringify(searchResults, null, 2)}</pre>
            </div>
          ) : (
            <div>
              <p>All applications:</p>
              <pre>{JSON.stringify(data, null, 2)}</pre>
            </div>
          )}
        </div>
      ) : (
        <p>Click the button to load data</p>
      )}
    </div>
  );
}

export default App;




