import React, { useState } from 'react';
import axios from 'axios';

function Raw() {

    const [data, setData] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');

    const handleButtonClick = () => {
        axios
            .get('https://engineering-task.elancoapps.com/api/raw')
            .then((response) => {
                setData(response.data);
            })
            .catch((error) => {
                console.error(error);
            });
    };

    const handleSearchChange = (event) => {
        setSearchTerm(event.target.value);
    };

    const filteredData = data
        ? data.filter((item) =>
            item['app-name'].toLowerCase().includes(searchTerm.toLowerCase())
        )
        : null;

    return (
        <div className='main'>
            <h1>Raw Data</h1>
            <div className='search'>
                <label htmlFor='search'>Search by app-name: </label>
                <input type='text' id='search' onChange={handleSearchChange} />
            </div>
            <button className='btn' onClick={handleButtonClick}>
                Get Data
            </button>

            {filteredData ? (
                <pre>{JSON.stringify(filteredData, null, 2)}</pre>
            ) : null}
        </div>
    );
}

export default Raw;




