import '../Css/Applications.css';
import React, { useState } from 'react';
import axios from 'axios';

function Applications() {
    const [loading, setLoading] = useState(false);
    const [applications, setApplications] = useState([]);
    const [selectedApplication, setSelectedApplication] = useState(null);
    const [applicationDetail, setApplicationDetail] = useState(null);
    const [showApp, setShowApp] = useState(true);
    const [filterDate, setFilterDate] = useState('');
    const [filterPrice, setFilterPrice] = useState('');
    const [sortOption, setSortOption] = useState('');

    const displayApplication = async (sortOption) => {
        setLoading(true);
        const params = {};
        if (filterDate) {
            params.date = filterDate;
        }
        if (filterPrice) {
            params.price = filterPrice;
        }
        if (sortOption) {
            params.sort = sortOption;
        }
        const response = await axios.get('https://engineering-task.elancoapps.com/api/applications', { params });
        setApplications(response.data);
        setLoading(false);
    };

    const chooseApplication = async (application) => {
        setSelectedApplication(application);
        setLoading(true);
        const params = {};
        if (filterDate) {
            params.date = filterDate;
        }
        if (filterPrice) {
            params.price = filterPrice;
        }
        const response = await axios.get(`https://engineering-task.elancoapps.com/api/applications/${application}`, { params });
        setApplicationDetail(response.data);
        setLoading(false);
        setShowApp(false);
    };

    const goBack = () => {
        setSelectedApplication(null);
        setApplications([]);
        setShowApp(true);
    };

    const handleSortOption = (e) => {
        setSortOption(e.target.value);
    }

    return (
        <div className='main'>
            <h1>Applications</h1>
            {showApp && (
                <>
                    <div>
                        <label>
                            Filter by Date:
                            <input type='text' value={filterDate} onChange={(e) => setFilterDate(e.target.value)} />
                        </label>
                        <label>
                            Filter by Price:
                            <input type='text' value={filterPrice} onChange={(e) => setFilterPrice(e.target.value)} />
                        </label>
                    </div>
                    <div>
                        <label>
                            Sort by:
                            <select value={sortOption} onChange={handleSortOption}>
                                <option value=''>None</option>
                                <option value='date'>Date</option>
                                <option value='price'>Price</option>
                            </select>
                        </label>
                    </div>
                    <button className='btn' onClick={() => displayApplication(sortOption)}>Load Applications</button>
                </>
            )}
            {loading ? (
                <p>Loading...</p>
            ) : showApp ? (
                <ul>
                    {applications.map((application) => (
                        <li key={application}>
                            {application}{" "}
                            <button className='btn' onClick={() => chooseApplication(application)}>
                                View Details
                            </button>
                        </li>
                    ))}
                </ul>
            ) : (
                <div>
                    <h2>{selectedApplication}</h2>
                    <button className='btn' onClick={goBack}>Go Back</button>
                    {loading ? (
                        <p>Loading...</p>
                    ) : applicationDetail ? (
                        <div>
                            <pre>{JSON.stringify(applicationDetail, null, 2)}</pre>
                        </div>
                    ) : (
                        <p>Loading application information...</p>
                    )}
                </div>
            )
            }
        </div >
    );
}

export default Applications;








