import React, { useState } from 'react';
import axios from 'axios';

function Raw() {

    const [data, setData] = useState(null);

    const handleButtonClick = () => {
        axios.get("https://engineering-task.elancoapps.com/api/raw")
            .then((response) => {
                // Sort the data by date
                const sortedByDate = response.data.sort((a, b) => {
                    return new Date(a.date) - new Date(b.date);
                });

                // Sort the data by price
                const sortedByPrice = response.data.sort((a, b) => {
                    return a.price - b.price;
                });

                // Set the sorted data to the state
                setData({ sortedByDate, sortedByPrice });
            })
            .catch((error) => {
                console.error(error);
            });
    };

    return (
        <div className="main">
            <h1>Raw Data</h1>
            <button className="btn" onClick={handleButtonClick}>
                Get Data
            </button>

            {data ? (
                <div>
                    <h2>Sorted by Date</h2>
                    <pre>{JSON.stringify(data.sortedByDate, null, 2)}</pre>
                    <h2>Sorted by Price</h2>
                    <pre>{JSON.stringify(data.sortedByPrice, null, 2)}</pre>
                </div>
            ) : null}
        </div>
    );
}

export default Raw;




