import '../Css/Resources.css';
import React, { useState } from 'react';
import axios from 'axios';

function Resources() {
    //whether the data is being loaded from the API or not 
    const [loading, setLoading] = useState(false);

    //an array that returns the data from API 
    const [resources, setResources] = useState([]);

    //can be used to select specifc resources  
    const [selectedResources, setSelectedResources] = useState(null);

    //shows the details of the selected resources 
    const [resourcesDetail, setResourcesDetail] = useState([]);

    //indicates to whether display the the list of resources or just the details 
    const [showApp, setShowApp] = useState(true);



    //async function that sends request to get the list of resources
    const displayResources = async () => {
        setLoading(true);
        const response = await axios.get('https://engineering-task.elancoapps.com/api/resources');
        setResources(response.data);
        setLoading(false);
    };
    //async function that sends request to get the details of the selected resources
    const chooseResources = async (application) => {
        setSelectedResources(application);
        setLoading(true);
        const response = await axios.get(`https://engineering-task.elancoapps.com/api/resources/${application}`);
        setResourcesDetail(response.data);
        setLoading(false);
        setShowApp(false);
    };

    //allows to clear the selected resources, and load the orignal page 
    const goBack = () => {
        setSelectedResources(null);
        setResources([]);
        setShowApp(true);
    };




    return (

        <div className='main'>
            <h1>Resources</h1>

            {/* Render a button to retrieve data from API if showApp is true */}
            {showApp && <button className='btn' onClick={displayResources}>Load Resources</button>}

            {/* Renders a paragraph if loading is true */}
            {loading ? <p>Loading...</p> :

                //also, if showApp is true it renders a list of resources and a button to view the deatials of each resources.
                //.map allows us to iterate through the array
                showApp ? (
                    <ul>
                        {resources.map((resource) => (
                            <li >
                                {resource}{" "}
                                <button className='btn' onClick={() => chooseResources(resource)}>
                                    View Details
                                </button>
                            </li>
                        ))}
                    </ul>
                ) :

                    //if showApp is false, it dislays the details of the selected resources and a message as well as a go back button
                    <div>
                        <h2>{selectedResources}</h2>
                        <button className='btn' onClick={goBack}>Go Back</button>

                        {loading ? <p>Loading...</p> :
                            resourcesDetail ? (
                                <div>
                                    <ul>
                                        {resourcesDetail.map((resourceD,) => (
                                            <li>{resourceD}{" "}
                                            </li>
                                        ))}
                                    </ul>
                                </div>
                            ) : <p>Loading application information...</p>}
                    </div>
            }
        </div >
    );
};

export default Resources;
