import '../Css/Raw.css'
import React, { useState } from 'react';
import axios from 'axios';

function Raw() {
    //saves the data that is coming from the API
    const [data, setData] = useState(null);
    //adding the inital values for the sort fucntions
    const [sortOrder, setSortOrder] = useState({
        ConsumedQuantity: 'asc',
        Cost: 'asc'
    });
    //keeps track of the sort order e.g., are we in asc order or desc
    const [sortedData, setSortedData] = useState(null);

    //used axios to get the data from the API, then saved it in the setData and setSortedData
    const handleButtonClick = () => {
        axios.get('https://engineering-task.elancoapps.com/api/raw')
            .then(response => {
                setData(response.data);
                setSortedData(response.data);
            }).catch(error => {
                console.error(error);
            })
    };

    const sortByConsumedQuantity = () => {
        //to display to the users that what order their on 
        const order = sortOrder.ConsumedQuantity === 'asc' ? 'desc' : 'asc';
        //craeting a new array to save all the new sorted data by subtrackting a by b to get the desc and b by a to get asc 
        const sorted = sortedData.sort((a, b) => {
            return order === 'asc' ? a.ConsumedQuantity - b.ConsumedQuantity : b.ConsumedQuantity - a.ConsumedQuantity;
        });

        setSortedData(sorted);
        //overwrites the current state of sort order by the new state after it is sorted into asc or desc order.
        setSortOrder({ ...sortOrder, ConsumedQuantity: order });
    };

    const sortByCost = () => {
        const order = sortOrder.Cost === 'asc' ? 'desc' : 'asc';
        const sorted = sortedData.sort((x, y) => {
            return order === 'asc' ? x.Cost - y.Cost : x.Cost - y.Cost;
        })
        setSortedData(sorted);
        setSortOrder({ ...sortOrder, Cost: order });
    };



    return (
        <div className='main'>
            <h1>Raw Data</h1>
            <button className='rbtn' onClick={handleButtonClick}>Get Data</button>
            {sortedData ? (
                <>
                    <br />
                    <button className='rbtn' onClick={sortByConsumedQuantity}>
                        Sort by ConsumedQuantity ({sortOrder.ConsumedQuantity})
                    </button>
                    <button className='rbtn' onClick={sortByCost}>
                        Sort by Cost ({sortOrder.Cost})
                    </button>

                    <pre>{JSON.stringify(sortedData, null, 2)}</pre>
                </>
            ) : null}
        </div>
    );
}

export default Raw;



