import '../Css/Applications.css';
import React, { useState } from 'react';
import axios from 'axios';

function Applications() {
    const [loading, setLoading] = useState(false);
    const [applications, setApplications] = useState([]);
    const [selectedApplication, setSelectedApplication] = useState(null);
    const [applicationDetail, setApplicationDetail] = useState(null);
    const [showApp, setShowApp] = useState(true);
    const [priceFilter, setPriceFilter] = useState('');
    const [dateFilter, setDateFilter] = useState('');

    const displayApplication = async () => {
        setLoading(true);
        const response = await axios.get('https://engineering-task.elancoapps.com/api/applications');
        setApplications(response.data);
        setLoading(false);
    };

    const chooseApplication = async (application) => {
        setSelectedApplication(application);
        setLoading(true);
        const response = await axios.get(`https://engineering-task.elancoapps.com/api/applications/${application}`);
        setApplicationDetail(response.data);
        setLoading(false);
        setShowApp(false);
    };

    const goBack = () => {
        setSelectedApplication(null);
        setApplications([]);
        setShowApp(true);
    };

    const handlePriceFilterChange = (event) => {
        setPriceFilter(event.target.value);
    };

    const handleDateFilterChange = (event) => {
        setDateFilter(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        setLoading(true);
        const response = await axios.get('https://engineering-task.elancoapps.com/api/applications', {
            params: {
                price: priceFilter,
                date: dateFilter
            }
        });
        setApplications(response.data);
        setLoading(false);
    };

    return (
        <div className='main'>
            <h1>Applications</h1>
            {showApp && (
                <button onClick={displayApplication}>Load Applications</button>
            )}
            {!showApp && (
                <button onClick={goBack}>Go Back</button>
            )}
            <form onSubmit={handleSubmit}>
                <label htmlFor="price-filter">Price:</label>
                <input type="text" id="price-filter" value={priceFilter} onChange={handlePriceFilterChange} />
                <label htmlFor="date-filter">Date:</label>
                <input type="text" id="date-filter" value={dateFilter} onChange={handleDateFilterChange} />
                <button type="submit">Filter</button>
            </form>
            {loading ? <p>Loading...</p> : (
                showApp ? (
                    <ul>
                        {applications.map((application) => (
                            <li key={application}>
                                {application}{' '}
                                <button onClick={() => chooseApplication(application)}>View Details</button>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <div>
                        <h2>{selectedApplication}</h2>
                        {loading ? <p>Loading...</p> : applicationDetail ? (
                            <pre>{JSON.stringify(applicationDetail, null, 2)}</pre>
                        ) : (
                            <p>Loading application information...</p>
                        )}
                    </div>
                )
            )}
        </div>
    );
}

export default Applications;











