import '../Css/Applications.css';
import React, { useState } from 'react';
import axios from 'axios';


function Applications() {
    const [isLoading, setIsLoading] = useState(false);
    const [applications, setApplications] = useState([]);
    const [selectedApplication, setSelectedApplication] = useState(null);
    const [applicationInfo, setApplicationInfo] = useState(null);
    const [showApplications, setShowApplications] = useState(true);

    const loadApplications = async () => {
        setIsLoading(true);
        const response = await axios.get('https://engineering-task.elancoapps.com/api/applications');
        setApplications(response.data);
        setIsLoading(false);
    };

    const selectApplication = async (application) => {
        setSelectedApplication(application);
        setIsLoading(true);
        const response = await axios.get(`https://engineering-task.elancoapps.com/api/applications/${application}`);
        setApplicationInfo(response.data);
        setIsLoading(false);
        setShowApplications(false);
    };

    const goBack = () => {
        setSelectedApplication(null);
        setApplications([]);
        setShowApplications(true);
    };

    return (
        <div className='main'>
            <h1>Applications</h1>

            {showApplications && (
                <button onClick={loadApplications}>Load Applications</button>
            )}
            {isLoading ? <p>Loading...</p> : (

                showApplications ? (
                    <ul>
                        {applications.map((application) => (
                            <li key={application}>
                                {application}{' '}
                                <button onClick={() => selectApplication(application)}>View Details</button>
                            </li>
                        ))}
                    </ul>

                ) : (
                    <div>
                        <h2>{selectedApplication}</h2>
                        {isLoading ? <p>Loading...</p> : applicationInfo ? (
                            <pre>{JSON.stringify(applicationInfo, null, 2)}</pre>
                        ) : (
                            <p>Loading application information...</p>
                        )}

                    </div>
                )
            )}
        </div>
    );
}

export default Applications;






