import React, { useState } from 'react';
import axios from 'axios';

function Raw() {

    const [data, setData] = useState(null);

    const handleButtonClick = async () => {
        const response = await axios.get('https://engineering-task.elancoapps.com/api/raw');
        setData(response.data);
    };

    const sortDataByPrice = () => {
        if (data) {
            const sortedData = [...data].sort((a, b) => a.price - b.price);
            setData(sortedData);
        }
    };

    return (
        <div className='main'>
            <h1>Raw Data</h1>
            <button className='btn' onClick={handleButtonClick}>Get Data</button>
            <button className='btn' onClick={sortDataByPrice}>Sort by Price</button>
            {data && (
                <pre>{JSON.stringify(data, null, 2)}</pre>
            )}
        </div>
    );
}

export default Raw;




