import React, { useState } from 'react';
import axios from 'axios';

function Raw() {
    const [data, setData] = useState(null);

    const handleButtonClick = async () => {
        const response = await axios.get('https://engineering-task.elancoapps.com/api/raw');
        setData(response.data);
    };

    const sortByName = () => {
        const sortedData = [...data].sort((a, b) => a.name.localeCompare(b.name));
        setData(sortedData);
    };

    const sortByPrice = () => {
        const sortedData = [...data].sort((a, b) => a.price - b.price);
        setData(sortedData);
    };

    return (
        <div className='main'>
            <h1>Raw Data</h1>
            <button className='btn' onClick={handleButtonClick}>Get Data</button>
            {data && (
                <div>
                    <button className='btn' onClick={sortByName}>Sort by Name</button>
                    <button className='btn' onClick={sortByPrice}>Sort by Price</button>
                    <table>
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Price</th>
                            </tr>
                        </thead>
                        <tbody>
                            {data.map((item) => (
                                <tr key={item.id}>
                                    <td>{item.name}</td>
                                    <td>{item.price}</td>
                                    <pre>{JSON.stringify(data, null, 2)}</pre>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );

}

export default Raw;




